mapper.select:

    select count(*) from contract where 
    contractNum = #{contractNum,jdbcType=VARCHAR}
    and delFlg = '0'
    
      and id != #{id,jdbcType=VARCHAR}

SELECT a.serialNum AS Id, a.contractNum, a.firstParty, a.secondParty, a.contractType, a.serviceModel, a.signDate, a.signer, a.startDate, a.endDate, a.versionNO, a.status, a.creator, (SELECT comName FROM company WHERE comId=a.comId) AS comId FROM contract a
WHERE a.delFlg='0'
and contractType = #{contractType,jdbcType=VARCHAR}
