mapper.select:
SELECT a.serialNum,a.deliverNum,a.orderSerial,b.orderNum,COUNT(DISTINCT c.serialNum) AS materielCount,a.packageCount, a.receiver,(SELECT address FROM warehouse WHERE serialNum=a.warehouseSerial) AS deliveryAddress,a.status, (SELECT address FROM warehouse WHERE serialNum=d.warehouseSerial) AS takeAddress,a.deliverDate, e.transportType,a.remark,com.comName AS supplyName FROM delivery a LEFT JOIN orderInfo b ON a.orderSerial=b.serialNum LEFT JOIN deliveryMateriel c ON a.serialNum=c.deliverSerial LEFT JOIN takeDelivery d ON a.serialNum=d.deliverSerial LEFT JOIN deliveryTransport e ON e.deliverSerial=a.serialNum LEFT JOIN company com ON com.comId=a.supplyComId WHERE a.delFlg='0' AND a.creator=#{creator}
and a.supplyComId in
#{item}
GROUP BY(a.serialNum) ORDER BY a.createTime DESC
