mapper.update:
UPDATE delivery SET orderSerial=#{orderSerial}, deliverNum=#{deliverNum},
buyComId=#{buyComId},
warehouseSerial=#{warehouseSerial}, deliverAddress=#{deliveryAddress}, status=#{status}, shipper=#{shipper}, receiver=#{receiver}, maker=#{maker}, approval=#{approval}, approvalDate=#{approvalDate}, remark=#{remark}, deliverDate=#{deliverDate}, materielCount=#{materielCount}, packageCount=#{packageCount}, packageType=#{packageType}, packageSpecifications=#{packageSpecifications}, materielWeight=#{materielWeight}, serviceMoney=#{serviceMoney}, deliverer=#{deliverer}, contactNum=#{contactNum}, deliverRemark=#{deliverRemark}, reason=#{reason}, processInstanceId=#{processInstanceId}, userId=#{userId}, updater=#{updater}, updateTime=NOW() WHERE serialNum=#{serialNum}

mapper.select:
SELECT a.serialNum,a.deliverNum,a.orderSerial,b.orderNum,COUNT(DISTINCT c.serialNum) AS materielCount,a.packageCount, a.receiver,(SELECT address FROM warehouse WHERE serialNum=a.warehouseSerial) AS deliveryAddress,a.status, (SELECT address FROM warehouse WHERE serialNum=d.warehouseSerial) AS takeAddress,a.deliverDate, e.transportType,a.remark,com.comName AS supplyName,com1.comName AS buyName FROM delivery a LEFT JOIN orderInfo b ON a.orderSerial=b.serialNum LEFT JOIN deliveryMateriel c ON a.serialNum=c.deliverSerial LEFT JOIN takeDelivery d ON a.serialNum=d.deliverSerial LEFT JOIN deliveryTransport e ON e.deliverSerial=a.serialNum LEFT JOIN company com ON com.comId=a.supplyComId LEFT JOIN company com1 ON com1.comId=a.buyComId WHERE a.delFlg='0'
AND a.creator=#{creator}
and a.supplyComId in
#{item}
GROUP BY(a.serialNum) ORDER BY a.createTime DESC
