mapper.select:
select IFNULL(dm.serialNum,dm2.serialNum) as serialNum, IFNULL(dm.stockCount,dm2.stockCount) as stockCount, IFNULL(dm.batchNum,dm2.batchNum) as batchNum, IFNULL(dm.orderMaterielSerial,dm2.orderMaterielSerial) as orderMaterielSerial, IFNULL(dm.supplyMaterielSerial,dm2.supplyMaterielSerial) as supplyMaterielSerial, s.serialNum as s_serialNum, s.deliverSerial, s.takeDeliverSerial,s.shipperOrReceiver,s.inOutNum,s.inOutType,s.stockDate, s.operator, s.contactNum,s.docNum, s.status, s.remark, s.delFlg, s.creator, s.createTime, s.updater, s.updateTime, d.supplyComId,d.deliverNum,o.orderNum,o.buyComId,o.orderNum as orderNum from stockInOutRecord s left join delivery d on d.serialNum = s.deliverSerial left join deliveryMateriel dm on d.serialNum = dm.deliverSerial and dm.stockInOutRecordSerial is null and dm.delFlg = 0 left join deliveryMateriel dm2 on s.serialNum = dm2.stockInOutRecordSerial and dm2.deliverSerial = '' and dm2.delFlg = 0 left join orderInfo o on o.serialNum = d.orderSerial
and s.status= #{status,jdbcType=VARCHAR}
order by ${orderByClause}

select IFNULL(dm.serialNum,dm2.serialNum) as serialNum, IFNULL(dm.stockCount,dm2.stockCount) as stockCount, IFNULL(dm.batchNum,dm2.batchNum) as batchNum, IFNULL(dm.orderMaterielSerial,dm2.orderMaterielSerial) as orderMaterielSerial, IFNULL(dm.supplyMaterielSerial,dm2.supplyMaterielSerial) as supplyMaterielSerial, s.serialNum as s_serialNum, s.deliverSerial, s.takeDeliverSerial,s.shipperOrReceiver,s.inOutNum,s.inOutType,s.stockDate, s.operator, s.contactNum,s.docNum, s.status, s.remark, s.delFlg, s.creator, s.createTime, s.updater, s.updateTime, d.supplyComId,d.deliverNum,o.orderNum,o.supplyComId as buyComId,o.orderNum as orderNum,td.serialNum as takeDeliverSerial, m.serialNum as materielSerial from stockInOutRecord s left join takeDelivery td on td.serialNum = s.takeDeliverSerial LEFT JOIN delivery d ON d.serialNum = td.deliverSerial left join deliveryMateriel dm on td.serialNum = dm.deliverSerial and dm.stockInOutRecordSerial is null and dm.delFlg = 0 left join deliveryMateriel dm2 on s.serialNum = dm2.stockInOutRecordSerial and dm2.deliverSerial = '' and dm2.delFlg = 0 left join orderInfo o on o.serialNum = d.orderSerial
left join orderMateriel om ON om.serialNum = IFNULL(dm.orderMaterielSerial,dm2.orderMaterielSerial) left join materiel m on om.materielSerial = m.serialNum
left join orderMateriel om ON om.serialNum = IFNULL(dm.orderMaterielSerial,dm2.orderMaterielSerial) left join materiel m on om.materielSerial = m.serialNum LEFT JOIN buyMateriel bm on m.materielId = bm.materielId LEFT JOIN supplyMateriel sm on m.materielId = sm.materielId
and s.status= #{status,jdbcType=VARCHAR}
AND (sm.supplyComId = #{comId,jdbcType=VARCHAR} or bm.buyComId = #{comId,jdbcType=VARCHAR})
order by ${orderByClause}

select count(s.serialNum) from stockInOutRecord s left join takeDelivery td on td.serialNum = s.takeDeliverSerial LEFT JOIN delivery d ON d.takeDeliverSerial = td.serialNum left join deliveryMateriel dm on dm.deliverSerial = d.serialNum
and s.status= #{status,jdbcType=VARCHAR}
AND (sm.supplyComId = #{comId,jdbcType=VARCHAR} or bm.buyComId = #{comId,jdbcType=VARCHAR})

mapper.update:
update deliveryMateriel
SET
  qualifiedCount = #{qualifiedCount},
  unqualifiedCount = #{unqualifiedCount}
where serialNum = #{serialNum}
