SELECT MAX(s.stockDate) FROM stockoutbatch sob LEFT JOIN deliveryMateriel dm ON dm.serialNum = sob.stockOutMaterielSerial LEFT JOIN delivery d ON d.serialNum = dm.deliverSerial LEFT JOIN stockInOutRecord s ON d.serialNum = s.deliverSerial LEFT JOIN orderMateriel om ON om.serialNum = dm.orderMaterielSerial LEFT JOIN orderInfo o ON o.serialNum = om.orderSerial LEFT JOIN stock stock ON stock.materielSerial = om.materielSerial AND ( stock.manageType = '1' AND o.orderType != '委托采购'
)
WHERE stock.serialNum = #{serialNum,jdbcType=VARCHAR}

SELECT SUM(a.outCount ) from ( SELECT DISTINCT sob.serialNum, sob.outCount FROM stockoutbatch sob LEFT JOIN deliveryMateriel dm ON dm.serialNum = sob.stockOutMaterielSerial LEFT JOIN delivery d ON d.serialNum = dm.deliverSerial LEFT JOIN stockInOutRecord s ON d.serialNum = s.deliverSerial LEFT JOIN orderMateriel om ON om.serialNum = dm.orderMaterielSerial LEFT JOIN orderInfo o ON o.serialNum = om.orderSerial LEFT JOIN stock stock ON stock.materielSerial = om.materielSerial AND ( stock.manageType = '1' AND o.orderType != '委托采购'
)
WHERE stock.materielSerial = #{serialNum,jdbcType=VARCHAR})a
